mapper.update:
update t_board set
title = #{title},
content = #{content}
Where bno = #{bno}

update t_board set
 view_count = view_count + 1
Where bno = #{bno}

mapper.select:
SELECT 
*
FROM (
    SELECT rownum rnum, e.*
    FROM (SELECT *
        FROM T_BOARD tm 

        	WHERE WRITER LIKE concat(concat('%',#{searchValue}),'%')

        ORDER BY BNO desc
    ) e
    WHERE rownum BETWEEN 1 AND #{end}
) o
WHERE o.rnum BETWEEN #{start} AND #{end}
