mapper.update:
update tb_member set
passwd = #{passwd}
where userid = #{userid}

mapper.select:
SELECT 
*
FROM (
    SELECT rownum rnum, e.* 
    FROM (SELECT *
        FROM TB_MEMBER tm

        	WHERE name LIKE concat(concat('%',#{searchValue}),'%')

        ORDER BY tm.REGIDATE desc
    ) e
    WHERE rownum BETWEEN 1 AND #{end}
) o
WHERE o.rnum BETWEEN #{start} AND #{end}
